#!/bin/perl -Tw

use Data::Dumper;

$value_to_check = q{};
$width = 13;
$max_product = 0;

for my $index (1..(1001-$width)) {
    $chunk = substr($value_to_check,$index-1,$width);
    $value = product_of_digits($chunk);
    if ($value > $max_product) {
        $max_product = $value;
    }
}
print "Maximum product: $max_product\n";

sub product_of_digits {
    my ($string_of_digits) = @_;

    my @digits = split //, $string_of_digits;
    $product = 1;
    for my $value (@digits) {
        $product = $product * int($value);
    }
    return $product;
}
